import React, {Component} from "react";
import axios from 'axios';
import emaployee from "../login/Login";
import './Login.css';
import { Route, Redirect } from 'react-router'
export default class Login extends Component{
    constructor(props){
        super(props);
this.state={
    email:"",
    password:"",
    loginErrors:""
}
this.handleSubmit=this.handleSubmit.bind(this);
this.handleChange=this.handleChange.bind(this);
    };
        handleChange(event){
            this.setState({
                [event.target.name]: event.target.value
            });
           
        }
        handleSubmit(event){
            const {email,password} =this.state;
            var api="http://employeecrud.azurewebsites.net/api/values/LoginEmployee" + "/" + email + "/" + password;
          axios.post(api)
          .then(response => {
            console.log(response.data);
          })
          .catch(error => {
            console.log(error);
          });
          window.location.href="../login/Login.js"
         
        }
        render()
        {
            return(
                <div class="Login">  
                 <h1>Login Here</h1>
                    <form>
                    <p>Username</p>
                    <input 
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={this.state.email}
                    onChange={this.handleChange}
                    required
                    />
                     <p>Password</p>
                     <input 
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={this.state.password}
                    onChange={this.handleChange}
                    required
                    />
                    <button type="sumbit" onClick={this.handleSubmit}>Login</button>
                    </form>

                </div>
            )
        }
}
